import React from 'react';
import Star from './Star';

class Rating extends React.Component{

    state = {
        clicked:false,
        numOfRatedStar:0
    }

    starClick = (num)=>{
        this.setState({clicked:true, numOfRatedStar:num});
    }

    selectColor = ()=>{
        if(this.state.numOfRatedStar >= 0){
            const rate = {...this.state };
                  rate.numOfRatedStar=rate.numOfRatedStar-1;
             this.setState({numOfRatedStar:rate-1});
            return 'red';
        } else {
            return '#D3D3D3';
        }
    }

    render(){

      let stars = [1,2,3,4,5].map(val=><Star key={val} 
        setRating={this.props.setRating} 
        rating={val} 
        isClicked={this.state.clicked}
        starClick={()=>this.starClick(val)}
        color = {this.selectColor()}
        />);

        return (
            <div style={{marginLeft:'35%'}}>
               {stars}
            </div>
        );
    }
}

export default Rating;
