import React from 'react';
import Star from './Star';

class Rating extends React.Component{

    state = {
        clicked:false,
        numOfRatedStar:-1,
        colors : ['#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3']
    }

    starClick = (num)=>{
        let colors = [ ...this.state.colors ];

        this.setState({clicked:true, numOfRatedStar:num});
    }

    selectColor = (index)=>{
        let colors= ['#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3'];
        if(this.state.numOfRatedStar >= 0){
           colors = colors.map((item,i)=>{
               if(i <= index){
                   return 'red';
               }else{
                   return item;
               }
           });
        } 
            return colors[index];
    }

    render(){

      let stars = [1,2,3,4,5].map((val,index)=><Star key={val} 
        setRating={this.props.setRating} 
        rating={val} 
        isClicked={this.state.clicked}
        starClick={()=>this.starClick(val)}
        color = {this.state.colors[index]}
        />);

        return (
            <div style={{marginLeft:'35%'}}>
               {stars}
            </div>
        );
    }
}

export default Rating;
