import React from 'react';
import Star from './Star';

class Rating extends React.Component{

    state = {
        clicked:false
    }

    render(){

      let stars = [1,2,3,4,5].map(val=><Star key={val} setRating={this.props.setRating} rating={val} isCl/>);

        return (
            <div style={{marginLeft:'35%'}}>
               {stars}
            </div>
        );
    }
}

export default Rating;
