import React from 'react';

const star = (props)=>{
   
   const clicked = ()=>{
         return ()=>{
            props.starClick();
            return props.setRating('1',2,props.rating)};
   }
   if(props.isClicked){
    s = (<strong style={{color:'red'}}>{'\u2605'}</strong>);
   }
    return (
    <span style={{fontSize:'xx-large'}} onClick={clicked()}>{s}</span>
    );
}
//style={{color:'#D3D3D3',fontSize:'xx-large'}}
export default star;
