import React from 'react';
import Star from './Star';

class Rating extends React.Component{

    state = {
        clicked:false,
        numOfRateStar:0
    }

    starClick = (num)=>{
        this.setState({clicked:true, numOfRateStar:num});console.log
    }
    render(){

      let stars = [1,2,3,4,5].map(val=><Star key={val} 
        setRating={this.props.setRating} 
        rating={val} 
        isClicked={this.state.clicked}
        starClick={()=>this.starClick(val)}
        color = {'#D3D3D3'}
        />);

        return (
            <div style={{marginLeft:'35%'}}>
               {stars}
            </div>
        );
    }
}

export default Rating;
