import React from 'react';
import Star from './Star';

class Rating extends React.Component{

    state = {
        clicked:false,
        numOfRatedStar:-1,
        colors : ['#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3','#D3D3D3']
    }

    starClick = (num)=>{
        let updateColors = [ ...this.state.colors ], color='#D3D3D3';
        if(!this.state){

        }
            updateColors = updateColors.map((item,i)=>{
                    if(i <= num-1){
                        return 'red';
                    }else{
                        return item;
                    }
                });
        this.setState({clicked:true, numOfRatedStar:num, colors:updateColors});
    }

    render(){

      let stars = [1,2,3,4,5].map((val,index)=><Star key={val} 
        setRating={this.props.setRating} 
        rating={val} 
        starClick={this.starClick}
        color = {this.state.colors[index]}
        />);

        return (
            <div style={{marginLeft:'35%'}}>
               {stars}
            </div>
        );
    }
}

export default Rating;
