import React,{ Component } from 'react';
import Aux from '../../hoc/Auxilary'

class CommentBox extends Component{

    state = {
        comment:""
    }

    render(){
        
        return (
            <Aux>
                <textarea rows="4" cols="50" style={{}}></textarea>
                <button onClick={()=>this.props.setComment('1',3,'check')}>Submit</button>              
            </Aux>
        );
    }
}

export default CommentBox;
