        s = (<strong style={{color:'red'}}>{'\u2605'}</strong>);
        return ()=>{props.setRating('1',2,props.rating)};
       }
        return ()=>{props.setRating('1',2,props.rating)};
   }
    return (
    <span style={{fontSize:'xx-large'}} onClick={clicked()}>{s}</span>
    );
}
